const previewImage = document.getElementById("preview-image");
const messiButton = document.getElementById("messi-button");
const ronaldoButton = document.getElementById("ronaldo-button");
const neymarButton = document.getElementById("neymar-button");

messiButton.addEventListener("click", () => {
  const randomIndex = Math.floor(Math.random() * playerImages.messi.length);
  previewImage.src = playerImages.messi[randomIndex];
});

ronaldoButton.addEventListener("click", () => {
  const randomIndex = Math.floor(Math.random() * playerImages.ronaldo.length);
  previewImage.src = playerImages.ronaldo[randomIndex];
});

neymarButton.addEventListener("click", () => {
  const randomIndex = Math.floor(Math.random() * playerImages.neymar.length);
  previewImage.src = playerImages.neymar[randomIndex];
});
